import React, { useState, useContext, useEffect, useMemo } from 'react';
import './Map.css';
import Square from '../Square/Square';
import MenuContext from '../MenuContext';

function Map(props) {
    const [menuState, dispatch] = useContext(MenuContext);
    const [state, setState] = useState({
        rows: 15,
        cols: 50,
        grid: new Array(15*50).fill({val: false, type: null}),//space-time O(1)
        itemState: menuState.itemState
    })

    const memoizedMenuState = useMemo(() => ({menuState, dispatch}), [menuState, dispatch]);
    const memoizedState = useMemo(() => ({state, setState}), [state, setState])

    //If item button is changed
    useEffect(() => {
        memoizedState.setState((memoizedState) => {
            return {
                ...memoizedState.state,
                itemState: memoizedMenuState.menuState.itemState
            }
        })
    }, [memoizedMenuState.menuState.itemState]);

    //If menu clear button is clicked
    useEffect(() => {
        if(memoizedMenuState.menuState.clear === true){
            memoizedState.setState((memoizedState) => {
                return {
                    ...memoizedState.state,
                    grid: new Array(memoizedState.state.rows*memoizedState.state.cols).fill({val: false, type: null})
                }
            })

            return dispatch({type: "cleared"});
        }
    }, [memoizedMenuState.menuState.clear, dispatch]);

    useEffect(() => {
        if(memoizedMenuState.menuState.run === true){
            //Visualize path finding algorithm logic

            return dispatch({type: "complete"});
        }
    }, [memoizedMenuState.menuState.run, dispatch]);

    const renderSquare = (x,y,val) => {
        return (
            <Square
                //Minus 4 accounts for width and height
                width={
                    memoizedState.state.cols > memoizedState.state.rows
                        ? (props.winDimensions.width / memoizedState.state.cols) - 4
                        : (props.winDimensions.width / memoizedState.state.cols) * (Math.min((props.winDimensions.width / memoizedState.state.cols),(props.winDimensions.height / memoizedState.state.rows)) / Math.max((props.winDimensions.width / memoizedState.state.cols),(props.winDimensions.height / memoizedState.state.rows))) - 4 
                }
                height={
                    memoizedState.state.rows === memoizedState.state.cols
                        ? (props.winDimensions.height / memoizedState.state.rows) - 4
                        //converting aspect ration of the square to 1:1 by multiplying the height by minimum of the width and height divided by the maximum of the width and height
                        : (props.winDimensions.height / memoizedState.state.rows) * (Math.min((props.winDimensions.width / memoizedState.state.cols),(props.winDimensions.height / memoizedState.state.rows)) / Math.max((props.winDimensions.width / memoizedState.state.cols),(props.winDimensions.height / memoizedState.state.rows))) - 4
                }
                x={x}
                y={y}
                val={val}
                squareClick={(x, y) => handleClick(x, y)}
                itemState={memoizedState.state.itemState}
            />
        )
    }

    const renderMap = () => {
        //space-time O(1)
        const rows = Array(memoizedState.state.rows);
        //each row will contain an array of 50 available indexes
        const cols = Array(memoizedState.state.rows);

        //time O(n)
        for(let i = 0; i < memoizedState.state.grid.length; i++){
            if(i % memoizedState.state.cols === 0){
                cols[i / memoizedState.state.cols] = Array(memoizedState.state.cols)//space-time O(1)

                rows[i] = (
                    <div className="grid-row" key={i / memoizedState.state.cols}>
                        {cols[i / memoizedState.state.cols]}
                    </div>
                )
            }

            let square = (
                <div className="grid-col" key={i}>
                    {renderSquare(i % memoizedState.state.cols, Math.abs((i - (i % memoizedState.state.cols)) / memoizedState.state.cols), memoizedState.state.grid[i].val)}
                </div>
            )

            cols[(i - (i % memoizedState.state.cols)) / memoizedState.state.cols][i % memoizedState.state.cols] = square;
        }

        const map = rows;

        return map;
    }

    const handleClick = (x,y) => {
        switch (memoizedState.state.itemState) {
            case "start":
                memoizedState.setState(memoizedState => ({
                    ...memoizedState.state,
                    //state object is immutable so updates have to be done this way
                    grid: memoizedState.state.grid.map((square, index) => {
                        if(index === x + (y * memoizedState.state.cols)){
                            // console.log(x,y,square.type,memoizedState.state.itemState);
                            if(square.type === memoizedState.state.itemState || !square.val){
                                return square.val
                                    ? {...square, val: false, type: null}
                                    : {...square, val: true, type: state.itemState}
                            }
                        }

                        if(square.type === state.itemState && square.val && !state.grid[x + (y * state.cols)].val){
                            return {...square, val: false}
                        }

                        return {...square}
                    })
                }));

                break;

            case "wall":
                memoizedState.setState(state => ({
                    ...state,
                    //state object is immutable so updates have to be done this way
                    grid: state.grid.map((square, index) => {
                        if(index === x + (y * state.cols)){
                            // console.log(x,y,square.type,state.itemState);
                            if(square.type === state.itemState || !square.val){
                                return square.val
                                    ? {...square, val: false, type: null}
                                    : {...square, val: true, type: state.itemState}
                            }
                        }

                        return {...square}
                    })
                }));

                break;

            case "end":
                memoizedState.setState(state => ({
                    ...state,
                    //state object is immutable so updates have to be done this way
                    grid: state.grid.map((square, index) => {
                        if(index === x + (y * state.cols)){
                            // console.log(x,y,square.type,state.itemState);
                            if(square.type === state.itemState || !square.val){
                                return square.val
                                    ? {...square, val: false, type: null}
                                    : {...square, val: true, type: state.itemState}
                            }
                        }

                        return {...square}
                    })
                }));

                break;

            default:
                break;
        }

        return;
    }



    return (
        <div id="map">
            {console.time()}
                {renderMap()}
            {console.timeEnd()}
        </div>
    )
}

export default Map;
