import React, { useState, useEffect } from 'react';
import './Sandbox.css';

function Square() {
    return (
        <div className="square">

        </div>
    )
}

function Map(props) {
    const [state, setState] = useState();
    const grid = new Array(50).fill(0).map(() => new Array(50).fill(0));

    const renderSquare = (x,y,val) => {
        return (
            <Square 
                width={props.winDimensions / 50}
                height={props.win}
            />
        )
    }

    return (
        <div className="map">
            {grid.map((col, i) => {
                col.map((row, j) => {
                    return(
                        <div key={(i*50)+(j+1)}>
                            {renderSquare(i, j, row)}
                        </div>
                    ) 
                })
            })}
        </div>
    )
}

function Sandbox() {
    const [winDimensions, setWinDimensions] = useState({ width: window.innerWidth, height: window.innerHeight });

    useEffect(() => {
        const handleWinResize = () => {
            setWinDimensions({
                width: window.innerWidth,
                height: window.innerHeight
            })
        }

        window.addEventListener('resize', handleWinResize);

        console.log("Window has been resized: ", winDimensions.width, winDimensions.height);

        return () => {
            window.removeEventListener('resize', handleWinResize);
        };
    })

    return (
        <div id="sandbox">
            <Map 
                winDimensions={winDimensions}
            />
        </div>
    );
}

export default Sandbox;
