import React, { useEffect, useContext, useState } from 'react';
import './Account.css';
import UserContext from "../UserContext";
import { useQuery } from '@apollo/client';
import MapHelper from '../Helpers/MapHelper';
import { Link } from 'react-router-dom';

function Account() {
  const {user} = useContext(UserContext);
  const { error: mapsError, data: mapsData } = useQuery(MapHelper.getUsersMaps, {
    variables: {
      username: user.username
    }
  }); //{ fetchPolicy: "network-only" }
  const [maps, setMaps] = useState(); 

  const userMaps = maps.map((map, index) => {
    <Link to="/sandbox">
      <div key={index}>
          <h3>{map.name}</h3>
      </div>
    </Link>
  })

  useEffect(() => {
    if(mapsError){
      console.log(mapsError);
    }

    if(mapsData){
      console.log(mapsData.getUsersMaps);

      setMaps((maps) => {
        return mapsData.getUsersMaps;
      })
    }
  }, [user, mapsError, mapsData]);

  return (
    <div id="account">
      <h1>Account</h1>
    </div>
  );
}

export default Account;
