import React from 'react';
import { Route, Redirect } from 'react-router-dom';

const RouteGuard = ({ component: Component, user, ...rest}) => (
    <Route {...rest} render={((props) => {
        switch (props.history.location.pathname){
            case "/login":
                console.log(user)
                return (
                    user
                        ? <Component {...props} />
                        : <Redirect to='/home' />
                )
            default:
                return (
                    !user 
                        ? <Component {...props} />
                        : <Redirect to='/login' />
                )
        }
    })} />
)

