import React, { useState, useEffect } from 'react';
import { Route, Redirect } from 'react-router-dom';

//Passing component as Component for rendering as component, and ..rest as all other passed variables
//TODO: Fix bug where route guard runs before the user is populated causing the app to redirect to login then redirect to home on refreshes
const RouteGuard = ({ component: Component, user, logout, updateUser, location, ...rest}) => {
    useEffect(() => {
        console.log("User updated: ", user)
    }, [user]);

    return (
        <Route {...rest} render={((props) => {
            console.log(props)
            switch (props.history.location.pathname){
                //If user is logged in trying to access the login page they get redirected to home page
                case "/login":
                    return (
                        <Component 
                            {...props}
                            logout={logout}
                            updateUser={updateUser}
                            location={() => {
                                props.location.state = {
                                    from: props.location
                                }

                                return props
                            }}
                        />
                    )

                //If user is not logged in they get redirected to login page
                default:
                    return (
                        user
                            ? <Component 
                                {...props}
                            />
                            : <Redirect to='/login' />
                    )
            }
        })} />
    )
}

export default RouteGuard;
