import React, { useContext } from 'react';
import './Menu.css';
import MenuContext from "../MenuContext";

function Menu(){
    const [menuState, setMenuState] = useState({
        itemState: "start",
        clear: false,
        run: false
    });

    const menu = useMemo(() => ([menuState, setMenuStat]), [menuState, setMenuState]);

    const switchItemType = (itemType) => {
        setMenuState({
            ...menuState,
            itemState: itemType,
        })
    }

    const clearMap = () => {
        setMenuState({
            ...menuState,
            clear: true,
        })
    }

    const run = () => {
        setMenuState({
            ...menuState,
            run: true,
        })
    }

    return (
        <MenuContext.Provider value={menu}>
            <div className="menu">
                <button onClick={() => switchItemType("start")}>Start Item</button>
                <button onClick={() => switchItemType("wall")}>Wall Item</button>
                <button onClick={() => switchItemType("end")}>End Item</button>
                <button onClick={() => clearMap()}>Clear</button>
                <button onClick={() => run()}>Run</button>
            </div>
        </MenuContext.Provider>
    )
}

export default Menu;
