import React, { useState, useEffect } from 'react';
import './Menu.css';
import MenuContext from "../MenuContext";

function Menu(){
    const [menu, setMenu] = useContext(MenuContext);

    const switchItemType = (itemType) => {
        setState({
            ...state,
            itemType: itemType,
        })
    }

    const clearMap = () => {
        setState({
            ...state,
            grid: new Array(state.rows*state.cols).fill({val: false, type: null})
        })
    }

    return (
        <div className="menu">
            <button onClick={() => switchItemType("start")}>Start Item</button>
            <button onClick={() => switchItemType("wall")}>Wall Item</button>
            <button onClick={() => switchItemType("end")}>End Item</button>
            <button onClick={() => clearMap()}>Clear</button>
            <button>Run</button>
        </div>
    )
}

export default Menu;
