import React, { useState, useEffect } from 'react';
import './Sandbox.css';

function Square(props) {
    const [state, setState] = useState({x: props.x, y: props.y, val: props.val});
    console.log(state);
    useEffect(() => {
        setState({
            ...state,
            val: props.val
        })
    }, [props.val])

    return (
        <div style={{width:props.width, height:props.height}} className="square">

        </div>
    )
}

function Map(props) {
    const grid = new Array(50).fill(0).map(() => new Array(50).fill(0));

    const renderSquare = (x,y,val) => {
        return (
            <Square 
                width={props.winDimensions.width / 50}
                height={props.winDimensions.height / 50}
                x={x}
                y={y}
                val={val}
                onclick={(x,y) => {console.log(x,y)}}
            />
        )
    }

    return (
        <div className="map">
            {grid.map((col, j) => {
                return(
                    <div className="row">
                        {col.map((row, i) => {
                            return(
                                <div className="col" key={(i*50)+(j+1)}>
                                    {renderSquare(i, j, row)}
                                </div>
                            ) 
                        })}
                    </div>
                )
            })}
        </div>
    )
}

function Sandbox() {
    const [winDimensions, setWinDimensions] = useState({ width: window.document.documentElement.clientWidth, height: window.document.documentElement.clientHeight });

    useEffect(() => {
        const handleWinResize = () => {
            setWinDimensions({
                width: window.document.documentElement.clientWidth,
                height: window.document.documentElement.clientHeight
            })
        }

        window.addEventListener('resize', handleWinResize);

        console.log("Window has been resized: ", winDimensions.width, winDimensions.height);

        return () => {
            window.removeEventListener('resize', handleWinResize);
        };
    })

    return (
        <div id="sandbox">
            <Map 
                winDimensions={winDimensions}
            />
        </div>
    );
}

export default Sandbox;
