import { ApolloClient, InMemoryCache, HttpLink } from '@apollo/client';

const httpLink = new HttpLink({
    uri: 'http://localhost:3000/graphql',
});

export default new ApolloClient({
    uri: httpLink,
    cache: new InMemoryCache()
});
