import { gql, useQuery } from '@apollo/client';

const loginQuery = gql(`
                        query login {
                            login(username: "Menz_Couli", password: "worldwidmenz"){
                                response_type,
                                response,
                                username,
                                accessToken
                            }
                        }
                    `)
const AuthHelper = {
    login: useQuery(loginQuery)
}

export default AuthHelper;
