import React from 'react';
import './LoginPage.css';
import AuthHelper from '../Auth Helpers/AuthHelper';
import { useLazyQuery, useMutation } from '@apollo/client';

const LoginForm = (props) => {
  const [login, { loading, error, data }] = useLazyQuery(AuthHelper.login);
  const [signup, { signupLoading, signupError, signupData }] = useMutation(AuthHelper.signup);

  if(error){
    console.log(error);
    let formRef = document.getElementsByClassName("form");
    let errorParagraphRef = document.getElementsByClassName("error-paragraph");

    if(errorParagraphRef.length !== 0 ){
      formRef.remove(errorParagraphRef);
    }

    let errorParagragh = document.createElement("P");
    let errorMessage = document.createTextNode("Unable to log you in at this time");

    errorParagragh.classList.add("error-paragraph");
    errorParagragh.appendChild(errorMessage);

    formRef.appendChild(errorParagragh);

    return null;
  };

  if(loading){
    console.log("...loading");
  }

  if(data){
    if(data.login.response_type.includes("Error")){
      let formRef = document.forms[0];
      let errorParagraphRef = document.getElementsByClassName("error-paragraph");



      let errorParagragh = document.createElement("P");
      let errorMessage = document.createTextNode("Invalid login credentials");

      errorParagragh.classList.add("error-paragraph");
      errorParagragh.appendChild(errorMessage);

      formRef.appendChild(errorParagragh);

      return null;
    };

    return null;
  }

  if(props.loginOption === "login"){
    return (
      <form class="login">
        <div class="form-group">
          <label for="loginUsername">Username</label>
          <input type="username" class="form-control" id="loginUsername" aria-describedby="usernameHelp" placeholder="Enter Username"></input>
        </div>
        <div class="form-group">
          <label for="loginPassword">Password</label>
          <input type="password" class="form-control" id="loginPassword" placeholder="Password"></input>
        </div>
        <br></br>
        <button type="button" class="btn btn-primary" onClick={
            (e) => login(
            {
              variables: {
                username: e.target.form.elements[0].value,
                password: e.target.form.elements[1].value
              }
            },
            console.log(`"${e.target.form.elements[0].value}"`, `"${e.target.form.elements[1].value}"`),)
          }>Submit</button>
      </form>
    );
  }

  if(props.loginOption === "signup"){
    return (
      <form class="signup">
        <div class="form-group">
          <label for="signupUsername">Username</label>
          <input type="username" class="form-control" id="signupUsername" aria-describedby="usernameHelp" placeholder="Enter Username"></input>
        </div>
        <div class="form-group">
          <label for="signupEmail">Email address</label>
          <input type="email" class="form-control" id="signupEmail" aria-describedby="emailHelp" placeholder="Enter Email"></input>
          <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div class="password">
          <div class="form-group">
            <label for="signupPassword1">Password</label>
            <input type="password" class="form-control" id="signupPassword1" placeholder="Password"></input>
          </div>
          <div class="form-group">
            <label for="signupPassword2">Confirm Password</label>
            <input type="password" class="form-control" id="signupPassword2" placeholder="Confirm Password"></input>
          </div>
        </div>
        <button type="button" class="btn btn-primary" onClick={
            (e) => signup({
              //have this call a function beforehand here to validate passwords and form elements

              variables: {
                username: e.target.form.elements[0],
                email: e.target.form.elements[1],
                password: e.target.form.elements[2]
              }
            })
          }>Submit</button>
      </form>
    );
  }

  if(props.loginOption === "guest"){
    return (
      <form class="guest">
        <div class="form-group">
          <label for="guestUsername">Username</label>
          <input type="username" class="form-control" id="guestUsername" aria-describedby="usernameHelp" placeholder="Enter Username"></input>
        </div>
        <button type="button" class="btn btn-primary" onClick={
            (e) => login({
              variables: {
                username: e.target.form.elements[0],
                password: ""
              }
            })
          }>Submit</button>
      </form>
    );
  }
}

class LoginPage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      loginOption: "login",
    }
  }

  renderForm(){
    if(this.state.loginOption === "login"){
      return (
        <LoginForm 
          loginOption={this.state.loginOption}
        />
      );
    }
    // register($username: String!, $email: String!, $password: String!)
    if(this.state.loginOption === "signup"){
      return (
        <LoginForm 
          loginOption={this.state.loginOption}
        />
      );
    }

    if(this.state.loginOption === "guest"){
      return (
        <LoginForm 
          loginOption={this.state.loginOption}
        />
      );
    }
  }

  onSubmit(e){
    console.log(e.target.form.elements);

    return false;
  }

  selectLoginOption(e, loginOption){
    // see if this becomes a problem later
    document.forms[0].reset();

    let allLoginButtons = document.getElementsByClassName("login-options")[0].childNodes;

    allLoginButtons.forEach((button) => {
      button.classList.remove("active");
    });

    e.target.closest("button").classList.add("active");

    //React Re-renders when this.setState is called.
    this.setState({
      loginOption: loginOption
    })

    return false;
  }

  render() {
    return (
      <div id="login-page">
        <div class="login-container">

          {/* Login form */}
          {this.renderForm()}

          <div class="login-options">
            <button class="login-btn btn active" onClick={($event) => this.selectLoginOption($event, `login`)}>
              <h1>Login</h1>
            </button>
            <button class="signup-btn btn" onClick={($event) => this.selectLoginOption($event, `signup`)}>
              <h1>Sign Up</h1>
            </button>
            <button class="guest-btn btn" onClick={($event) => this.selectLoginOption($event, `guest`)}>
              <h1>Guest</h1>
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default LoginPage;
