import React, { useState, useEffect } from 'react';
import './Sandbox.css';

function Square(props) {
    const [state, setState] = useState({x: props.x, y: props.y, val: props.val});
    useEffect(() => {
        setState({
            ...state,
            val: props.val
        })
    }, [props.val])

    return (
        <div style={{width: `${props.width}px`, height:`${props.height}px`}} className="square" onClick={() => props.onClick(state.x,state.y)}>

        </div>
    )
}

function Map(props) {
    const handleClick = (x,y) => {
        console.log(x,y);
        return
    }

    const grid = new Array(30).fill(0).map(() => new Array(30).fill(0));

    const renderSquare = (x,y,val) => {
        return (
            <Square 
                width={props.winDimensions.width / grid.length}
                height={props.winDimensions.height /  grid.length}
                x={x}
                y={y}
                val={val}
                onClick={(x,y) => handleClick(x,y)}
            />
        )
    }

    return (
        <div className="map">
            {grid.map((col, j) => {
                return(
                    <div className="grid-row" key={j}>
                        {col.map((row, i) => {
                            return(
                                <div className="grid-col" key={j+ (i* grid.length)}>
                                    {renderSquare(i, j, row)}
                                </div>
                            ) 
                        })}
                    </div>
                )
            })}
        </div>
    )
}

function Sandbox() {
    const [winDimensions, setWinDimensions] = useState({ width: document.documentElement.clientWidth, height: document.documentElement.clientHeight/1.25 });

    useEffect(() => {
        const handleWinResize = () => {
            setWinDimensions({
                width: document.documentElement.clientWidth,
                height: Math.min(document.documentElement.clientHeight/1.05)
            })
        }

        window.addEventListener('resize', handleWinResize);

        console.log("Window has been resized: ", winDimensions.width, winDimensions.height);

        return () => {
            window.removeEventListener('resize', handleWinResize);
        };
    })

    return (
        <div id="sandbox">
            <Map 
                winDimensions={winDimensions}
            />
        </div>
    );
}

export default Sandbox;
