import { gql, useQuery } from '@apollo/client';

const AuthHelper = {
    login: (username, password = "") => {
        return gql(`
            query login {
                login(username: "${username}", password: "${password}"){
                    response_type,
                    response,
                    username,
                    accessToken
                }
            }
        `)
    }
}

export default AuthHelper;
