import React, { useState, useContext } from 'react';
import './Map.css';
import Square from '../Square/Square';
import MenuContext from '../MenuContext';

function Map(props) {
    const [menuState, dispatch] = useContext(MenuContext);
    const [state, setState] = useState({
        rows: 15,
        cols: 50,
        grid: new Array(15*50).fill({val: false, type: null}),//space-time O(1)
        itemType: "start"
    })

    use

    const renderSquare = (x,y,val) => {
        return (
            <Square
                //Minus 4 accounts for width and height
                width={
                    state.cols > state.rows
                        ? (props.winDimensions.width / state.cols) - 4
                        : (props.winDimensions.width / state.cols) * (Math.min((props.winDimensions.width / state.cols),(props.winDimensions.height / state.rows)) / Math.max((props.winDimensions.width / state.cols),(props.winDimensions.height / state.rows))) - 4 
                }
                height={
                    state.rows === state.cols
                        ? (props.winDimensions.height / state.rows) - 4
                        //converting aspect ration of the square to 1:1 by multiplying the height by minimum of the width and height divided by the maximum of the width and height
                        : (props.winDimensions.height / state.rows) * (Math.min((props.winDimensions.width / state.cols),(props.winDimensions.height / state.rows)) / Math.max((props.winDimensions.width / state.cols),(props.winDimensions.height / state.rows))) - 4
                }
                x={x}
                y={y}
                val={val}
                squareClick={(x, y) => handleClick(x, y)}
                itemType={state.itemType}
            />
        )
    }

    const renderMap = () => {
        //space-time O(1)
        const rows = Array(state.rows);
        //each row will contain an array of 50 available indexes
        const cols = Array(state.rows);

        //time O(n)
        for(let i = 0; i < state.grid.length; i++){
            if(i % state.cols === 0){
                cols[i / state.cols] = Array(state.cols)//space-time O(1)

                rows[i] = (
                    <div className="grid-row" key={i / state.cols}>
                        {cols[i / state.cols]}
                    </div>
                )
            }

            let square = (
                <div className="grid-col" key={i}>
                    {renderSquare(i % state.cols, (i - (i % state.cols)) / state.cols, state.grid[i].val)}
                </div>
            )

            cols[(i - (i % state.cols)) / state.cols][i % state.cols] = square;
        }

        const map = rows;

        return map;
    }

    const handleClick = (x,y) => {
        switch (state.itemType) {
            case "start":
                setState(state => ({
                    ...state,
                    //state object is immutable so updates have to be done this way
                    grid: state.grid.map((square, index) => {
                        if(index === x + (y * state.cols)){
                            // console.log(x,y,square.type,state.itemType);
                            if(square.type === state.itemType || !square.val){
                                return square.val
                                    ? {...square, val: false, type: null}
                                    : {...square, val: true, type: state.itemType}
                            }
                        }

                        if(square.type === state.itemType && square.val && !state.grid[x + (y * state.cols)].val){
                            return {...square, val: false}
                        }

                        return {...square}
                    })
                }));

                break;

            case "wall":
                setState(state => ({
                    ...state,
                    //state object is immutable so updates have to be done this way
                    grid: state.grid.map((square, index) => {
                        if(index === x + (y * state.cols)){
                            // console.log(x,y,square.type,state.itemType);
                            if(square.type === state.itemType || !square.val){
                                return square.val
                                    ? {...square, val: false, type: null}
                                    : {...square, val: true, type: state.itemType}
                            }
                        }

                        return {...square}
                    })
                }));

                break;

            case "end":
                setState(state => ({
                    ...state,
                    //state object is immutable so updates have to be done this way
                    grid: state.grid.map((square, index) => {
                        if(index === x + (y * state.cols)){
                            // console.log(x,y,square.type,state.itemType);
                            if(square.type === state.itemType || !square.val){
                                return square.val
                                    ? {...square, val: false, type: null}
                                    : {...square, val: true, type: state.itemType}
                            }
                        }

                        return {...square}
                    })
                }));

                break;

            default:
                break;
        }

        return;
    }



    return (
        <div id="map">
            {console.time()}
                {renderMap()}
            {console.timeEnd()}
        </div>
    )
}

export default Map;
