import { ApolloClient, InMemoryCache } from '@apollo/client';

const httpLink = new HttpLink({ uri: 'https://api.example.com/graphql' });

export default new ApolloClient({
    uri: 'http://localhost:3000/graphql',
    cache: new InMemoryCache({
        addTypename: false
    }),
    credentials: 'include'
});
