import MinHeap from './MinHeap';

function getIndex(x, y, cols){
    return ((x) * (y * cols));
}

function heuristic(point, endPoint){
    //TODO make this a switch for the different types of heuristic functions
    const distance = Math.sqrt(Math.pow((point.x-endPoint.x), 2) + Math.pow((point.y-endPoint.y), 2));  

    return [endPoint, distance];
} 

function getPoints(gridMap){
    let startPoint = null;
    let wallPoints = [];
    let endPoints = [];

    gridMap.map((square) => {
        if(square.val && square.type ===  "start"){
            // console.log(square);
            startPoint = square;

            return null;
        }

        if(square.val && square.type === "wall"){
            // console.log(square);
            wallPoints.push(square);

            return null;
        }

        if(square.val && square.type === "end"){
            // console.log(square);
            endPoints.push(square);

            return null;
        }

        return null;
    })

    return [startPoint, wallPoints, endPoints];
}

function getNeighbors(point, gridMap, cols){
    //Point                                 Index
    // left point.x-1, point.y              (point.x-1) + (point.y * cols)
    // right point.x+1, point.y             (point.x+1) + (point.y * cols)
    // up point.x, point.y-1                (point.x) + (point.y-1 * cols)
    // down point.x, point.y+1              (point.x) + (point.y+1 * cols)
    // top left point.x-1, point.y-1        (point.x-1) + (point.y-1 * cols)
    // top right point.x+1, point.y-1       (point.x+1) + (point.y-1 * cols)
    // bottom left point.x-1, point.y+1     (point.x-1) + (point.y+1 * cols)
    // bottom right point.x+1, point.y+1    (point.x+1) + (point.y+1 * cols)

    const left        = (point.x-1) + ((point.y) * cols);
    const right       = (point.x+1) + ((point.y) * cols);
    const up          = (point.x) + ((point.y-1) * cols);
    const down        = (point.x) + ((point.y+1) * cols);
    const topLeft     = (point.x-1) + ((point.y-1) * cols);
    const topRight    = (point.x+1) + ((point.y-1) * cols);
    const bottomLeft  = (point.x-1) + ((point.y+1) * cols);
    const bottomRight = (point.x+1) + ((point.y+1) * cols);

    const neighbors = new Array(8);

    //Grid was setup so whenever a point has a type it has a value otherwise it's null
    if(gridMap[left] && gridMap[left].type !== "wall"){
        neighbors[0] = gridMap[left];
    }
    if(gridMap[right] && gridMap[right].type !== "wall"){
        neighbors[1] = gridMap[right];
    }
    if(gridMap[up] && gridMap[up].type !== "wall"){
        neighbors[2] = gridMap[up];
    }
    if(gridMap[down] && gridMap[down].type !== "wall"){
        neighbors[3] = gridMap[down];
    }
    if(gridMap[topLeft] && gridMap[topLeft].type !== "wall"){
        neighbors[4] = gridMap[topLeft];
    }
    if(gridMap[topRight] && gridMap[topRight].type !== "wall"){
        neighbors[5] = gridMap[topRight];
    }
    if(gridMap[bottomLeft] && gridMap[bottomLeft].type !== "wall"){
        neighbors[6] = gridMap[bottomLeft];
    }
    if(gridMap[bottomRight] && gridMap[bottomRight].type !== "wall"){
        neighbors[7] = gridMap[bottomRight];
    }

    return neighbors;
}

function AStar(rows, cols, gridMap){
    const [startPoint, wallPoints, endPoints] = getPoints(gridMap);

    let points = {};
    let openSet = new MinHeap();
    let cameFrom = {};
    let gScore = {};
    let fScore = {};
    let goal = null;


    if(!startPoint || !endPoints){
        console.log("Map must have a start point, and an end point");
        return "Map must have a start point, and an end point"; 
    }

    gScore[startPoint.x + (startPoint.y * cols)] = 0;

    const endPointDistances = new Array(endPoints.length);

    endPoints.map((endPoint, i) => {
        const score = heuristic(startPoint, endPoint); 

        endPointDistances[i] = [score[0], gScore[startPoint.x + (startPoint.y * cols)] + score[1]];

        return null;
    })

    console.log(endPointDistances);

    endPointDistances.sort((a, b) => {
        if(a[1] > b[1]){
            return 1
        }
        if(a[1] < b[1]){
            return -1
        }

        return 0;
    });

    goal = endPointDistances[0][0];

    fScore[startPoint.x + (startPoint.y * cols)] = endPointDistances[0][1];

    points[fScore[startPoint.x + (startPoint.y * cols)]] = startPoint;
    openSet.insert(fScore[startPoint.x + (startPoint.y * cols)]);

    while(openSet.getHeap().length !== 0){
        const current = points[openSet.peek()];

        console.log(current);

        if(current.x === goal.x && current.y === goal.y){
            console.log("GOOOAAAAALLLLLLL")
            return;
        }

        const neighbors = getNeighbors(current, gridMap, cols);

        console.log(neighbors)
        openSet.extract();

        neighbors.map((neighbor) => {
            currentPathGScore = gScore[current.x ]
        });
    }

    return;
}

export default AStar
