import { gql } from '@apollo/client';
import { useQuery } from '@apollo/client';

class AuthHelper extends React.Component {
    constructor(props) {
        super(props);

    }

     loginOptions = {
        login: (username, password = "") => {
            return gql(`
                        query login {
                            login(username: "${username}", password: "${password}"){
                                response_type,
                                response,
                                username,
                                accessToken
                            }
                        }
                    `)
        }
    }
}

export default AuthHelper;
