import React, { useState, useMemo, useEffect } from "react";
import axios from "axios";
import apolloClient from './ApolloConfig'
import LoginPage from './Login Page/LoginPage';
import Home from './Home/Home';
import App from './App/App';
import { ApolloProvider } from '@apollo/client';
import { BrowserRouter as Router, Switch, Route, Redirect } from "react-router-dom";
import UserContext from './UserContext';
import RouteGuard from './RouteGuard';
import TopNav from "./TopNav/TopNav";

//Using containers so top nav is not rendered on login page
const LoginContainer = ({user, logout}) => {
    return(
        <div>
            <RouteGuard 
                path="/login" 
                component={LoginPage}
                user={user}
                logout={logout}
            />
        </div>
    )
}

const DefaultContainer = ({user, logout}) => {
    return(
        <div>
            <TopNav />
            <Route exact={true} path="/"><Redirect to="/home"/></Route>
            <RouteGuard 
                path='/app' 
                component={App} 
                user={user}
                logout={logout}
            />
            <RouteGuard 
                path='/home' 
                component={Home} 
                user={user}
                logout={logout}
            />
        </div>
    )
}

const updateUser = (userData, setUser) => {
    if(!userData.accessToken){
        setUser(null);

        return;
    }

    setUser({
      username: userData.username,
      role: userData.role,
      accessToken: userData.accessToken
    })

    return;
}

const logout = () => {
    updateUser(null);

    axios.get("http://localhost:3000/deleteRefreshToken")
        .then((response) => {
            updateUser(response.data);

            return;
        })
        .catch((error) => {
            console.log(error.data);
            return;
        });
}

function Routes() {
    //Stores user data if user is logged in and null if user isn't logged in
    const [user, setUser] = useState();

    console.log(user);

    //Adds headers to every axios request(requests)
    axios.interceptors.request.use(
        function(request) {
            request.headers["Content-Type"] = 'application/json; charset=utf-8';
            request.headers["Accept"] = 'application/json';
            request.headers["authorization"] = user ? `Bearer ${user.accessToken}` : "";
            request.withCredentials = true;
            return request;
        },
        function(error) {
            return Promise.reject(error);
        }
    );

    const value = useMemo(() => ([user, setUser]), [user, setUser]);

    //To make sure tokenRefresh attempt is only made once.
    useEffect(() => {
        axios.get("http://localhost:3000/refreshToken")
            .then((response) => {
                updateUser(response.data, setUser);

                return;
            })
            .catch((error) => {
                console.log(error.data);
                 return;
            });
    }, []);

    return (
        <Router>
            <UserContext.Provider value={value}>
                <ApolloProvider client={apolloClient}>
                    <Switch>
                        {/* Render is used to pass props to components rendered by React Router */}
                        <Route
                            exact 
                            path="/login" 
                            render={() => (
                                <LoginContainer
                                    user={user}
                                    logout={logout}
                                    updateUser
                                />
                            )}
                        />
                        <Route
                            render={() => (
                                <DefaultContainer
                                    user={user}
                                    logout={logout}
                                />
                            )}
                        />
                    </Switch>
                </ApolloProvider>
            </UserContext.Provider>
        </Router>
    );
};
export default Routes;
