import React, { useContext, useMemo } from 'react';
import './Menu.css';
import MenuContext from "../MenuContext";

function Menu(){
    const [menuState, dispatch] = useContext(MenuContext);

    const [state, setState] = useMemo(() => ([menuState, dispatch]), [[menuState, dispatch]])

    return (
        <div className="menu">
            <button onClick={() => setState({type: 'setItemState', payload: { itemState: "start" }})}>Start Item</button>
            <button onClick={() => setState({type: 'setItemState', payload: { itemState: "wall" }})}>Wall Item</button>
            <button onClick={() => setState({type: 'setItemState', payload: { itemState: "end" }})}>End Item</button>
            <button onClick={() => setState({type: 'clear'})}>Clear</button>
            <button onClick={() => setState({type: 'run'})}>Run</button>
        </div>
    )
}

export default Menu;
