import React, { useState, useEffect } from 'react';
import './Sandbox.css';

//TODO: Runs a little slow, find a way to optimize (possibly using hooks)
function Square(props) {
    let downFlag = false;
    const [state, setState] = useState({x: props.x, y: props.y, val: props.val, type: props.itemType});
    useEffect(() => {
        setState({
            ...state,
            val: props.val,
            type: props.itemType
        })
    }, [props.val]);

    // const handleMouseDown = () => {
    //     // props.squareClick(state.x,state.y);

    //     // document.addEventListener('mouseover', handler);

    //     // document.addEventListener('mouseup', handler);
    // }

    const handler = (e, x=null, y=null) => {
        if(e.type === "mousedown"){
            downFlag = true;
            // console.log(x,y);
            props.squareClick(x,y);
        }

        if(e.type === "mouseover"){
            if(downFlag){
                // console.log(x,y);
                props.squareClick(x, y);
            }
        }

        if(e.type === "mouseup"){
            downFlag = false;
        }
    }

    switch (state.type) {
        case "start":
            return (
                <div style={{width: `${props.width}px`, height:`${props.height}px`, backgroundColor: props.val === true ? `green` : `white`}} className="square" onMouseDown={(e) => handler(e,state.x,state.y)} onMouseUp={(e) => handler(e)} onMouseOver={(e) => handler(e,state.x,state.y)}></div>
            )

        case "wall":
            return (
                <div style={{width: `${props.width}px`, height:`${props.height}px`, backgroundColor: props.val === true ? `gray` : `white`}} className="square" onMouseDown={(e) => handler(e,state.x,state.y)} onMouseUp={(e) => handler(e)} onMouseOver={(e) => handler(e,state.x,state.y)}></div>
            )

        case "end":
            return (
                <div style={{width: `${props.width}px`, height:`${props.height}px`, backgroundColor: props.val === true ? `red` : `white`}} className="square" onMouseDown={(e) => handler(e,state.x,state.y)} onMouseUp={(e) => handler(e)} onMouseOver={(e) => handler(e,state.x,state.y)}></div>
            )

        default:
            break;
    }
}

function Map(props) {
    const [state, setState] = useState({
        rows: 25,
        cols:50,
        grid: new Array(25*50).fill({val: false, type: null}),
        itemType: "start"
    })

    const renderSquare = (x,y,val) => {
        return (
            <Square 
                width={(props.winDimensions.width / 50) / (50 / 25)}
                //This keeps ratio of squares so that they are square, they become longer if height becomes longer than the width (Square width is twice as long as square height)
                height={Math.min((props.winDimensions.height / 25), (props.winDimensions.width / 50) / (50 / 25))}
                x={x}
                y={y}
                val={val}
                squareClick={(x, y) => handleClick(x, y)}
                itemType={state.itemType}
            />
        )
    }

    const renderMapCols = (j) => {
        let cols = Array(50);

        for(let i = 0; i < 50; i++){
            cols
            .push(
                <div className="grid-col" key={i + (j * 50)}>
                    {renderSquare(i, j, state.grid[j + (i * 50)].val)}
                </div>
            )
        }

        return cols;
    }

    const renderMapRows = () => {
        let rows = Array(25)

        for(let j = 0; j < 25; j++){
            rows
            .push(
                <div className="grid-row" key={j}>
                    {renderMapCols(j)}
                </div>
            )
        }

        return rows;
    }

    const switchItemType = (itemType) => {
        setState({
            ...state,
            itemType: itemType,
        })
    }



    const handleClick = (x,y) => {
        switch (state.itemType) {
            case "start":
                setState(state => ({
                    ...state,
                    //state object is immutable so updates have to be done this way
                    grid: state.grid.map((square, index) => {
                            if(index === y + (x * 50)){
                                console.log(x,y,square.type,state.itemType);
                                if(square.type === state.itemType || !square.type){
                                    if(state.grid[y + (x * 50)].val){
                                        return {...square, val: !state.grid[y + (x * 50)].val, type: null}
                                    }

                                    return {...square, val: !state.grid[y + (x * 50)].val, type: "start"}
                                }
                            }
                            // return row.map((col, i) => {
                            //     if(y === j && x === i){
                            //         // console.log(x,y,col.type,state.itemType);
                            //         if(col.type === state.itemType || !col.type){
                            //             if(state.grid[y][x].val){
                            //                 return {...col, val: !state.grid[y][x].val, type: null}
                            //             }

                            //             return {...col, val: !state.grid[y][x].val, type: "start"}
                            //         }
                            //     }

                            //     if(col.type == "start"){
                            //         return {...col, val: false, type: null}
                            //     }

                            //     return {...col}
                            // })
                    })
                }));

                break;

            case "wall":
                setState(state => ({
                    ...state,
                    //state object is immutable so updates have to be done this way
                    grid: state.grid.map((row, j) => {
                            return row.map((col, i) => {
                                if(y === j && x === i){
                                    // console.log(x,y,col.type,state.itemType);
                                    if(col.type === state.itemType || !col.type){
                                        if(state.grid[y][x].val){
                                            return {...col, val: !state.grid[y][x].val, type: null}
                                        }

                                        return {...col, val: !state.grid[y][x].val, type: "wall"}
                                    }
                                }

                                return {...col}
                            })
                    })
                }));

                break;

            case "end":
                setState(state => ({
                    ...state,
                    //state object is immutable so updates have to be done this way
                    grid: state.grid.map((row, j) => {
                            return row.map((col, i) => {
                                if(y === j && x === i){
                                    // console.log(x,y,col.type,state.itemType);
                                    if(col.type === state.itemType || !col.type){
                                        if(state.grid[y][x].val){
                                            return {...col, val: !state.grid[y][x].val, type: null}
                                        }

                                        return {...col, val: !state.grid[y][x].val, type: "end"}
                                    }
                                }

                                return {...col}
                            })
                    })
                }));

                break;

            default:
                break;
        }

        return;
    }

    return (
        <div className="map">
            {renderMapRows()}
            <button onClick={() => switchItemType("start")}>Start Item</button>
            <button onClick={() => switchItemType("wall")}>Wall Item</button>
            <button onClick={() => switchItemType("end")}>End Item</button>
        </div>
    )
}

function Sandbox() {
    const [winDimensions, setWinDimensions] = useState({ width: document.documentElement.clientWidth, height: document.documentElement.clientHeight });

    useEffect(() => {
        const handleWinResize = () => {
            setWinDimensions({
                width: document.documentElement.clientWidth,
                height: document.documentElement.clientHeight
            })
        }

        window.addEventListener('resize', handleWinResize);

        // console.log("Window has been resized: ", winDimensions.width, winDimensions.height);

        return () => {
            window.removeEventListener('resize', handleWinResize);
        };
    })

    return (
        <div id="sandbox">
            <Map 
                winDimensions={winDimensions}
            />
        </div>
    );
}

export default Sandbox;
