import React from 'react';
import './LoginPage.css';

function LoginForm(props) {
  if(props.loginOption === "login"){
    return (
      <form class="login">
        <div class="form-group">
          <label for="username">Username</label>
          <input type="username" class="form-control" id="username" aria-describedby="usernameHelp" placeholder="Enter Username"></input>
        </div>
        <div class="form-group">
          <label for="password">Password</label>
          <input type="password" class="form-control" id="password" placeholder="Password"></input>
        </div>
        <br></br>
        <button type="submit" class="btn btn-primary">Submit</button>
      </form>
    );
  }

  if(props.loginOption === "signup"){
    return (
      <form class="signup">
        <div class="form-group">
          <label for="username">Username</label>
          <input type="username" class="form-control" id="username" aria-describedby="usernameHelp" placeholder="Enter Username"></input>
        </div>
        <div class="form-group">
          <label for="email">Email address</label>
          <input type="email" class="form-control" id="email" aria-describedby="emailHelp" placeholder="Enter Email"></input>
          <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div class="password">
          <div class="form-group">
            <label for="password1">Password</label>
            <input type="password" class="form-control" id="password1" placeholder="Password"></input>
          </div>
          <div class="form-group">
            <label for="password2">Confirm Password</label>
            <input type="password" class="form-control" id="password2" placeholder="Confirm Password"></input>
          </div>
        </div>
        <button type="submit" class="btn btn-primary">Submit</button>
      </form>
    );
  }

  if(props.loginOption === "guest"){
    return (
      <form class="signup">
        <div class="form-group">
          <label for="exampleInputEmail1">Username</label>
          <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"></input>
        </div>
        <button type="submit" class="btn btn-primary">Submit</button>
      </form>
    );
  }
}

class LoginPage extends React.Component{
  constructor(props) {
    super(props);

    this.state = {
      loginOption: "login"
    }
  }

  renderForm(){
    return (
      <LoginForm 
        loginOption={this.state.loginOption}
      />
    );
  }

  selectLoginOption(){
    this.setState({
      loginOption: "login"
    })
  }

  render() {
    return (
      <div id="login-page">
        <div class="login-container">

          {/* Login form */}
          {this.renderForm()}

          <div class="login-options">
            <div class="login-btn" onClick="selectLoginOption($event)">
              <h1>Login</h1>
            </div>
            <div class="signup-btn" onClick="selectLoginOption($event)">
              <h1>Sign Up</h1>
            </div>
            <div class="guest-btn" onClick={(guest) => this.selectLoginOption()}>
              <h1>Guest</h1>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default LoginPage;
