    const distance = Math.sqrt(Math.pow((endPoint.x-startPoint.x), 2) + Math.pow((endPoint.y-startPoint.y), 2));
    // const distance = Math.abs(startPoint.x-endPoint.x) +  Math.abs(startPoint.y-endPoint.y)

    return distance;
} 

function dist(startPoint, endPoint){
    //TODO make this a switch for the different types of heuristic functions
    const distance = Math.sqrt(Math.pow((endPoint.x-startPoint.x), 2) + Math.pow((endPoint.y-startPoint.y), 2));     

    return distance;
} 

function getPoints(gridMap){
    let startPoint = null;
    let wallPoints = [];
