            return null;
        }

        if(square.val && square.type === "end"){
            // console.log(square);
            endPoints.push(square);

            return null;
        }

        return null;
    })

    return [startPoint, wallPoints, endPoints];
}

function reconstructPath(path, state, setState, heap, points){
    setState({
        ...state,
        //state object is immutable so updates have to be done this way
        grid: state.grid.map((square, index) => {
            if(path[index]){
                if(square.type !== "start" && square.type !== "end" && square.type !== "path"){
                    return {...square, val: true, type: "neighbors"};
                }
