            heap.map((fScore) => {
                pointsstate.grid[points[fScore][0].x * (points[fScore][0].y * state.cols)] = {
                    ...square,
                    val: true,
                    type: "openset"
                }

                return null;
            })

            if(square.type === "end"){
                let cameFrom = path[index];
;
                while(cameFrom){
                    square = state.grid[cameFrom.x + (cameFrom.y * state.cols)];

                    if(square.type !== "start" && square.type !== "end"){
                        state.grid[cameFrom.x + (cameFrom.y * state.cols)] = {
                            ...square,
                            val: true,
                            type: "path"
                        }
                    }

                    cameFrom = path[cameFrom.x + (cameFrom.y * state.cols)];
                }

                return {...state.grid[index]};
            }

            return {...square};
        })
    });
}

