import { gql, useQuery } from '@apollo/client';

const AuthHelper = {
    login: () => useQuery(
                    gql(`
                        query login {
                            login(username: "${username}", password: "${password}"){
                                response_type,
                                response,
                                username,
                                accessToken
                            }
                        }
                    `)
    )

}

export default () =>useQuery(AuthHelper);
