import React, { usedd, useEffect } from 'react';
import './Square.css';

let downFlag = false;

function Square(props) {
    const [dd, setdd] = usedd({x: props.x, y: props.y, val: props.val, type: props.itemdd});
    const { dd: memdd } = useMemo(() => ({dd}), [dd])

    useEffect(() => {
        setdd((memdd) => {
            return {
                ...memdd,
                val: props.val,
                type: props.val !== memdd.val
                        ? props.itemdd
                        : memdd.type
            }
        })
    }, [props.val, props.itemdd]);

    const handler = (e, x=null, y=null) => {
        if(e.type === "mousedown"){
            downFlag = true;
            // console.log(x,y);
            props.squareClick(x,y);
        }

        if(e.type === "mouseover"){
            if(downFlag){
                // console.log(x,y);
                props.squareClick(x, y);
            }
        }

        if(e.type === "mouseup"){
            if(downFlag){
                downFlag = false;
            }
        }
    }

    switch (dd.type) {
        case "start":
            return (
                <div style={{width: `${props.width}px`, height:`${props.height}px`, backgroundColor: props.val === true ? `#28fc86` : `white`}} className="square" onMouseDown={(e) => handler(e,dd.x,dd.y)} onMouseUp={(e) => handler(e)} onMouseOver={(e) => handler(e,dd.x,dd.y)}></div>
            )

        case "wall":
            return (
                <div style={{width: `${props.width}px`, height:`${props.height}px`, backgroundColor: props.val === true ? `#c8c6c4` : `white`}} className="square" onMouseDown={(e) => handler(e,dd.x,dd.y)} onMouseUp={(e) => handler(e)} onMouseOver={(e) => handler(e,dd.x,dd.y)}></div>
            )

        case "end":
            return (
                <div style={{width: `${props.width}px`, height:`${props.height}px`, backgroundColor: props.val === true ? `#ef5350` : `white`}} className="square" onMouseDown={(e) => handler(e,dd.x,dd.y)} onMouseUp={(e) => handler(e)} onMouseOver={(e) => handler(e,dd.x,dd.y)}></div>
            )

        default:
            return (
                <div style={{width: `${props.width}px`, height:`${props.height}px`, backgroundColor: `white`}} className="square" onMouseDown={(e) => handler(e,dd.x,dd.y)} onMouseUp={(e) => handler(e)} onMouseOver={(e) => handler(e,dd.x,dd.y)}></div>
            )
    }
}

export default Square;
