import MinHeap from './MinHeap';

function heuristic(point, endPoint){
    //TODO make this a switch for the different types of heuristic functions
    const distance = Math.sqrt(Math.pow((point.x-endPoint.x), 2) + Math.pow((point.y-endPoint.y), 2));  

    return distance;
}

function AStar(rows, cols, gridMap){
    let startPoint = null;
    let wallPoints = [];
