function AStar(gridMap){
    let startPoint = null;
    let wallPoints = [];
    let endPoints = [];

    gridMap.map((square) => {
        if(square.val && square.type ===  "start"){
            // console.log(square);
            startPoint = square;

            return null;
        }

        if(square.val && square.type === "wall"){
            // console.log(square);
            wallPoints.push(square);

            return null;
        }

        if(square.val && square.type === "end"){
            // console.log(square);
            endPoints.push(square);

            return null;
        }

        return null;
    })

    console.log(startPoint, wallPoints, endPoints);
}

export default AStar
