import React, { useEffect, useContext, useState } from 'react';
import './Account.css';
import UserContext from "../UserContext";
import { useQuery } from '@apollo/client';
import MapHelper from '../Helpers/MapHelper';
import { Link } from 'react-router-dom';

function Account() {
  const {user} = useContext(UserContext);
  const { error, data} = useQuery(MapHelper.getUsersMaps, {
    variables: {
      username: user.username
    }
  }); //{ fetchPolicy: "network-only" }
  const [maps, setMaps] = useState(); 

  useEffect(() => {
    if(error){
      console.log(error);
    }

    if(data){
      console.log(data.getUsersMaps);

      const mapsData = Array.from(data.getUsersMaps);

      mapsData.map((mapData) => {
        return {
          ...mapData,
          map: mapData.map.forEach((square) => {

          })
        }
      })

      setMaps((maps) => {
        return mapsData;
      })
    }
  }, [user, error, data]);

  return (
    <div id="account">
      {user &&
        <h1>{user.username}</h1>
      }

      {maps && 
        maps.map((map, index) => {
          return (
            <Link key={index} to={{pathname: "/sandbox", state: { userMap: map.map }}}>
              <div>
                  <h3>{map.name}</h3>
              </div>
            </Link>
          )
        })
      }
    </div>
  );
}

export default Account;
